// src/components/ToggleSwitch.js
import React from 'react';

export default function ToggleSwitch({
  checked,
  onChange,
  leftLabel = 'Normal',
  rightLabel = 'Roast',
}) {
  return (
    <div
      className='inline-flex border rounded-full overflow-hidden cursor-pointer'
      onClick={onChange}
    >
      <div
        className={`px-4 py-2 text-sm font-medium ${
          !checked
            ? 'bg-blue-500 text-white' // Active left side (Normal Mode)
            : 'bg-gray-300 text-gray-700' // Inactive
        }`}
      >
        {leftLabel}
      </div>
      <div
        className={`px-4 py-2 text-sm font-medium ${
          checked
            ? 'bg-red-500 text-white' // Active right side (Roast Mode)
            : 'bg-gray-300 text-gray-700' // Inactive
        }`}
      >
        {rightLabel}
      </div>
    </div>
  );
}
