import { useState, useRef, useEffect } from 'react';
import { getRoast } from '../api';

const RoastBot = () => {
  const [userInput, setUserInput] = useState('');
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(false);
  const [isRoastMode, setIsRoastMode] = useState(false); // ðŸ”¥ Roast Mode Toggle
  const [voiceMode, setVoiceMode] = useState(false); // ðŸ”Š AI Speech Toggle
  const chatRef = useRef(null);

  useEffect(() => {
    chatRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  // ðŸ”Š AI Speaks if Voice Mode is ON
  const speakResponse = (text) => {
    if (!text || !voiceMode) return;
    const speech = new SpeechSynthesisUtterance(text);
    speech.lang = 'en-US';
    speech.rate = 1.1;
    speech.pitch = 1;
    speech.volume = 1;
    window.speechSynthesis.speak(speech);
  };

  // ðŸŽ¤ Speech-to-Text (User Voice Input)
  const handleVoiceInput = () => {
    const recognition =
      new window.webkitSpeechRecognition() || new window.SpeechRecognition();
    recognition.lang = 'en-US';
    recognition.start();

    recognition.onresult = (event) => {
      const transcript = event.results[0][0].transcript;
      setUserInput(transcript); // Set spoken text as input
    };

    recognition.onerror = (event) => {
      console.error('Speech Recognition Error:', event.error);
    };
  };

  // ðŸ§  AI Generates Response Based on Mode
  const sendMessage = async (message) => {
    if (!message.trim()) return;
    setLoading(true);

    // ðŸŽ­ Ensure AI knows which mode it's in
    const systemPrompt = isRoastMode
      ? 'You are a sarcastic, funny AI that playfully roasts the user. Only respond with witty and humorous insults. Do NOT answer normal questions.'
      : "You are a helpful AI assistant. Provide polite, informative, and clear answers to the user's questions. Do NOT roast the user.";

    const response = await getRoast(message, systemPrompt);

    setMessages([
      ...messages,
      { text: message, sender: 'user' },
      { text: response, sender: 'ai' },
    ]);
    setUserInput('');

    speakResponse(response); // ðŸ”Š AI speaks response if Voice Mode is ON
    setLoading(false);
  };

  return (
    <div className='flex flex-col max-w-lg mx-auto bg-gray-900 text-white p-6 rounded-lg shadow-md'>
      <h1 className='text-2xl font-bold text-center mb-4'>
        ðŸ”¥ AI RoastBot & Assistant ðŸ¤–
      </h1>

      {/* ðŸ”¥ Mode Toggle (Roast Mode â†” Normal Mode) */}
      <div className='flex justify-between items-center mb-4'>
        <label className='flex items-center cursor-pointer'>
          <input
            type='checkbox'
            className='hidden'
            checked={isRoastMode}
            onChange={() => setIsRoastMode(!isRoastMode)}
          />
          <div
            className={`w-10 h-5 flex items-center bg-gray-500 rounded-full p-1 transition duration-300 ${
              isRoastMode ? 'bg-red-500' : 'bg-blue-500'
            }`}
          >
            <div
              className={`w-4 h-4 bg-white rounded-full shadow-md transform transition duration-300 ${
                isRoastMode ? 'translate-x-5' : ''
              }`}
            ></div>
          </div>
          <span className='ml-2'>
            {isRoastMode ? 'ðŸ”¥ Roast Mode' : 'ðŸ¤– Normal Mode'}
          </span>
        </label>
      </div>

      {/* ðŸ”Š Voice Mode Toggle */}
      <div className='flex justify-between items-center mb-4'>
        <label className='flex items-center cursor-pointer'>
          <input
            type='checkbox'
            className='hidden'
            checked={voiceMode}
            onChange={() => setVoiceMode(!voiceMode)}
          />
          <div
            className={`w-10 h-5 flex items-center bg-gray-500 rounded-full p-1 transition duration-300 ${
              voiceMode ? 'bg-green-500' : ''
            }`}
          >
            <div
              className={`w-4 h-4 bg-white rounded-full shadow-md transform transition duration-300 ${
                voiceMode ? 'translate-x-5' : ''
              }`}
            ></div>
          </div>
          <span className='ml-2'>
            {voiceMode ? 'ðŸ”Š Voice ON' : 'ðŸ”‡ Voice OFF'}
          </span>
        </label>
      </div>

      {/* Chat Messages */}
      <div className='h-80 overflow-y-auto border border-gray-700 p-3 rounded-lg mb-4'>
        {messages.map((msg, index) => (
          <div
            key={index}
            className={`flex ${
              msg.sender === 'user' ? 'justify-end' : 'justify-start'
            }`}
          >
            <p
              className={`p-3 my-1 rounded-lg max-w-xs text-white ${
                msg.sender === 'user' ? 'bg-blue-500' : 'bg-gray-700'
              }`}
            >
              {msg.text}
            </p>
          </div>
        ))}
        <div ref={chatRef}></div>
      </div>

      {/* Input Field */}
      <textarea
        className='w-full p-2 border border-gray-600 rounded bg-gray-800 text-white'
        placeholder='Type or speak your message...'
        value={userInput}
        onChange={(e) => setUserInput(e.target.value)}
      ></textarea>

      {/* Buttons */}
      <div className='flex gap-2 mt-3'>
        <button
          className='flex-1 bg-green-500 hover:bg-green-600 text-white p-2 rounded'
          onClick={() => sendMessage(userInput)}
          disabled={loading || !userInput.trim()}
        >
          {loading ? 'Thinking...' : 'Send'}
        </button>
        <button
          className='flex-1 bg-purple-500 hover:bg-purple-600 text-white p-2 rounded'
          onClick={handleVoiceInput}
        >
          ðŸŽ¤ Speak
        </button>
      </div>
    </div>
  );
};

export default RoastBot;
