import React, { useState, useRef, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { getRoast } from '../api'; // <-- your existing API call

export default function OnboardingRoastBot() {
  // -------------------------
  // 1) TOUR / ONBOARDING STATE
  // -------------------------
  // We'll have steps:
  // 0 -> Show small "Hi, I'm Boobsey" popup
  // 1 -> Expand to full RoastBot UI
  // 2 -> Tooltip for Roast Mode toggle
  // 3 -> Tooltip for Voice Mode toggle
  // 4 -> Tooltip for "Send" button
  // 5 -> Tour finished or skipped
  const [tourStep, setTourStep] = useState(0);
  const nextStep = () => setTourStep((s) => s + 1);
  const skipTour = () => setTourStep(5);

  // -------------------------
  // 2) ROASTBOT STATE & LOGIC
  // -------------------------
  const [userInput, setUserInput] = useState('');
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(false);
  const [isRoastMode, setIsRoastMode] = useState(false);
  const [voiceMode, setVoiceMode] = useState(false);
  const chatRef = useRef(null);

  useEffect(() => {
    chatRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  // AI Speaks if Voice Mode is ON
  const speakResponse = (text) => {
    if (!text || !voiceMode) return;
    const speech = new SpeechSynthesisUtterance(text);
    speech.lang = 'en-US';
    speech.rate = 1.1;
    speech.pitch = 1;
    speech.volume = 1;
    window.speechSynthesis.speak(speech);
  };

  // Speech-to-Text (User Voice Input)
  const handleVoiceInput = () => {
    const recognition =
      new window.webkitSpeechRecognition() || new window.SpeechRecognition();
    recognition.lang = 'en-US';
    recognition.start();

    recognition.onresult = (event) => {
      const transcript = event.results[0][0].transcript;
      setUserInput(transcript);
    };

    recognition.onerror = (event) => {
      console.error('Speech Recognition Error:', event.error);
    };
  };

  // Send message to API
  const sendMessage = async (message) => {
    if (!message.trim()) return;
    setLoading(true);

    // Set system prompt based on roast mode
    const systemPrompt = isRoastMode
      ? `You are an AI comedian who ONLY gives funny and sarcastic roasts.
         You MUST NEVER answer questions or provide helpful information.
         Every response must be an insult, but it should be lighthearted and playful.`
      : `You are a professional AI assistant.
         Your job is to help users with clear, useful, and polite answers.
         YOU MUST NEVER roast or insult the user in any way.
         Do not make jokes unless asked.`;

    try {
      const response = await getRoast(message, systemPrompt);
      setMessages((prev) => [
        ...prev,
        { text: message, sender: 'user' },
        { text: response, sender: 'ai' },
      ]);
      if (voiceMode) speakResponse(response);
    } catch (error) {
      console.error('Error sending message:', error);
      setMessages((prev) => [
        ...prev,
        { text: message, sender: 'user' },
        { text: 'Oops, something went wrong.', sender: 'ai' },
      ]);
    }

    setUserInput('');
    setLoading(false);
  };

  // Toggle roast mode & reset chat
  const toggleRoastMode = () => {
    setIsRoastMode(!isRoastMode);
    setMessages([]);
  };

  // -------------------------
  // 3) TOOLTIP POSITIONS (example)
  // -------------------------
  // If your layout is stable, you can just approximate coords for the tooltips.
  // Or you can use refs + getBoundingClientRect().
  const [roastTogglePos] = useState({ top: 200, left: 100 });
  const [voiceTogglePos] = useState({ top: 250, left: 100 });
  const [sendButtonPos] = useState({ top: 480, left: 350 });

  return (
    <div className='relative min-h-screen bg-gray-100 p-4'>
      {/* ----------------------
          STEP 0: SMALL POPUP
         ----------------------*/}
      <AnimatePresence>
