              <button
                className='flex-1 bg-green-500 text-white p-2 rounded'
                onClick={() => sendMessage(userInput)}
                disabled={loading || !userInput.trim()}
              >
                Send
              </button>
              <button
                className='flex-1 bg-purple-500 text-white p-2 rounded'
                onClick={handleVoiceInput}
