      {/* If user skipped or finished (tourStep >= 5), show normal chat with no tour */}
      {tourStep >= 5 && (
        <div
          className='max-w-lg mx-auto bg-gray-900 text-white
                     p-6 rounded-lg shadow-md'
          style={{ marginTop: '100px' }}
