        {tourStep === 2 && (
          <motion.div
            className='fixed bg-white text-black p-2 rounded shadow z-50 w-44'
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            style={{
