// src/components/ToggleSwitch.js
import React from 'react';

export default function ToggleSwitch({
  checked,
  onChange,
  leftLabel = 'OFF',
  rightLabel = 'ON',
}) {
  return (
    <div className='flex items-center'>
      <div
        className='relative inline-flex items-center cursor-pointer'
        onClick={onChange}
      >
        {/* Background track with labels */}
        <div className='flex items-center justify-between bg-gray-600 w-36 h-10 rounded-full px-1 relative z-10'>
          {/* Left label: white when not checked, gray when checked */}
          <span
            className={`flex-1 text-center text-sm font-medium transition-colors duration-200 ${
              checked ? 'text-gray-400' : 'text-white'
            }`}
          >
            {leftLabel}
          </span>
          {/* Right label: white when checked, gray when not checked */}
          <span
            className={`flex-1 text-center text-sm font-medium transition-colors duration-200 ${
              checked ? 'text-white' : 'text-gray-400'
            }`}
          >
            {rightLabel}
          </span>
        </div>
        {/* The sliding thumb with its own color */}
        <div
          className={`
            absolute top-0 bottom-0 w-1/2 h-full rounded-full
            transition-transform duration-300 z-0
            ${checked ? 'translate-x-full bg-red-500' : 'bg-blue-500'}
          `}
        />
      </div>
      {/* Hidden checkbox for accessibility */}
      <input
        type='checkbox'
        className='sr-only'
        checked={checked}
        onChange={onChange}
      />
    </div>
  );
}
