// src/components/ToggleSwitch.js
import React from 'react';

export default function ToggleSwitch({
  checked,
  onChange,
  leftLabel = 'Normal',
  rightLabel = 'Roast',
}) {
  return (
    <div
      className='relative w-45 h-10 rounded-full bg-gray-300 cursor-pointer overflow-hidden'
      onClick={onChange}
    >
      {/* The sliding thumb */}
      <div
        className={`
          absolute top-0 left-0 w-1/2 h-full rounded-full transition-transform duration-300
          ${checked ? 'translate-x-full bg-red-500' : 'bg-blue-500'}
        `}
      ></div>
      {/* Text container */}
      <div className='relative flex items-center h-full px-2 justify-between'>
        <span
          className={`text-sm font-medium ${
            checked ? 'text-gray-700' : 'text-white'
          }`}
        >
          {leftLabel}
        </span>
        <span
          className={`text-sm font-medium ${
            checked ? 'text-white' : 'text-gray-700'
          }`}
        >
          {rightLabel}
        </span>
      </div>
    </div>
  );
}
